"use strict";
const { domSymbolTree } = require("jsdom/lib/jsdom/living/helpers/internal-constants");
const { TEXT_NODE } = require("jsdom/lib/jsdom/living/node-type");

exports.childTextContent = node => {
  let result = "";
  const iterator = domSymbolTree.childrenIterator(node);
  for (const child of iterator) {
    if (child.nodeType === TEXT_NODE) {
      result += child.data;
    }
  }
  return result;
};
