"use strict";

const HTMLElementImpl = require("jsdom/lib/jsdom/living/nodes/HTMLElement-impl").implementation;
const DefaultConstraintValidationImpl =
  require("jsdom/lib/jsdom/living/constraint-validation/DefaultConstraintValidation-impl").implementation;
const { mixin } = require("jsdom/lib/jsdom/utils");
const { getLabelsForLabelable } = require("jsdom/lib/jsdom/living/helpers/form-controls");

class HTMLOutputElementImpl extends HTMLElementImpl {
  constructor(args, privateData) {
    super(args, privateData);
    this._labels = null;
  }

  _barredFromConstraintValidationSpecialization() {
    return true;
  }

  get labels() {
    return getLabelsForLabelable(this);
  }
}

mixin(HTMLOutputElementImpl.prototype, DefaultConstraintValidationImpl.prototype);

module.exports = {
  implementation: HTMLOutputElementImpl
};
