"use strict";

const HTMLMediaElementImpl = require("jsdom/lib/jsdom/living/nodes/HTMLMediaElement-impl").implementation;
const { reflectURLAttribute } = require("jsdom/lib/jsdom/utils");

class HTMLVideoElementImpl extends HTMLMediaElementImpl {
  get poster() {
    return reflectURLAttribute(this, "poster");
  }

  set poster(value) {
    this.setAttribute("poster", value);
  }

  get videoWidth() {
    return 0;
  }

  get videoHeight() {
    return 0;
  }
}

module.exports = {
  implementation: HTMLVideoElementImpl
};
